{
  "rules": [
    {
      "rule_id": "final_paycheck_timing_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "final paycheck issuance",
        "parameters": {}
      },
      "source_text": "If an employee's employment status is not 'active', then the employee will be issued a final paycheck on the next regularly scheduled paydate.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Final Paycheck",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "meal_break_hourly_employees",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.pay_type",
          "operator": "eq",
          "value": "hourly"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 6
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "unpaid meal break",
        "parameters": {
          "break.type": "meal",
          "break.duration_minutes": 30
        }
      },
      "source_text": "If an employee's `employee.pay_type` is hourly and their `employee.shift_hours` is greater than 6, then Acme Corp. allows them to take one unpaid meal break of at least 30 minutes during their shift.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Employee Handbook",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_timing_001",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "terminated"
        },
        {
          "field": "employee.tenure_months",
          "operator": "lte",
          "value": 60
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "final paycheck",
        "parameters": {
          "timing": "next regularly scheduled payday"
        }
      },
      "source_text": "final paychecks for terminated employees, as defined by the cessation of an employee's active employment status, will be issued no later than the next regularly scheduled payday after the final day of employment, but only if the employee's tenure is less than or equal to 60 months.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Employee Handbook - Final Paycheck Distribution",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "meal_break_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gte",
          "value": 8
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "unpaid meal break",
        "parameters": {
          "break.duration_minutes": 30
        }
      },
      "source_text": "All non-exempt employees who work a shift of eight (8) or more hours are eligible for one (1) unpaid meal break of at least thirty (30) minutes in duration.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Meal and Rest Breaks",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_paycheck_voluntary_resignation_notice",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "voluntary"
        },
        {
          "field": "termination.notice_given",
          "operator": "eq",
          "value": true
        },
        {
          "field": "termination.notice_hours",
          "operator": "gte",
          "value": 72
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck payment upon resignation",
        "parameters": {}
      },
      "source_text": "when an employee *voluntarily* resigns their position with Innovision Solutions, Inc., with at least seventy-two (72) hours\u2019 prior notice to their supervisor or the Human Resources Department, the final paycheck is due and payable at the time of resignation.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Final Paycheck Distribution (California Employees)",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "ca_overtime_daily_001",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "Overtime pay at 1.5x regular rate",
        "parameters": {}
      },
      "source_text": "Non-exempt employees in California are entitled to overtime pay at a rate of one and one-half (1.5) times their regular rate of pay for all hours worked in excess of eight (8) hours in any workday",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Starlight Technologies Employee Handbook",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_act_notice_california",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "layoff.timeframe_days",
          "operator": "lte",
          "value": 30
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 days advance written notice prior to the date of the planned action.",
        "parameters": {
          "notice_period_days": 60,
          "notice_type": "written"
        }
      },
      "source_text": "For employees whose primary work location is in the State of California, notwithstanding the foregoing, the California WARN Act shall apply if a mass layoff, relocation, or termination affects 50 or more employees in a 30-day period, and if Acme Corp.'s total employment count is 75 or more employees company-wide. In such instances, Acme Corp. shall provide affected employees with written notice at least 60 calendar days prior to the date of the planned action.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Workforce Reduction and WARN Act Compliance",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "fmla_termination_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.leave_type",
          "operator": "eq",
          "value": "family"
        },
        {
          "field": "employee.leave_type",
          "operator": "eq",
          "value": "medical"
        },
        {
          "field": "employee.leave_type",
          "operator": "eq",
          "value": "parental"
        },
        {
          "field": "employee.leave_type",
          "operator": "eq",
          "value": "bonding"
        },
        {
          "field": "leave.duration_weeks",
          "operator": "eq",
          "value": 12
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "terminated"
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "resigned"
        }
      ],
      "condition_logic": "any",
      "action": {
        "type": "require",
        "subject": "Employee termination",
        "parameters": {}
      },
      "source_text": "However, if an employee fails to return to work upon exhaustion of their FMLA leave, and the reason for not returning is that the employee has sought or accepted other employment, the employee's employment with Sterling Dynamics Inc. will be terminated effective the day after the last day of approved FMLA leave.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Family and Medical Leave (FMLA)",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "meal_break_entitlement_ca",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 5
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "unpaid meal break",
        "parameters": {
          "break.duration_minutes": 30,
          "break.type": "meal"
        }
      },
      "source_text": "Employees working shifts longer than five (5) hours are entitled to an unpaid meal break of at least thirty (30) minutes. In California, if an employee works more than ten (10) hours in a workday, a second unpaid meal break of at least thirty (30) minutes is required.",
      "confidence": "high"
    },
    {
      "rule_id": "second_meal_break_ca",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 10
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "second unpaid meal break",
        "parameters": {
          "break.duration_minutes": 30,
          "break.type": "meal"
        }
      },
      "source_text": "In California, if an employee works more than ten (10) hours in a workday, a second unpaid meal break of at least thirty (30) minutes is required.",
      "confidence": "high"
    },
    {
      "rule_id": "rest_break_entitlement_hourly",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.pay_type",
          "operator": "eq",
          "value": "hourly"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "paid rest break",
        "parameters": {
          "break.duration_minutes": 10,
          "break.type": "rest"
        }
      },
      "source_text": "Acme Corp provides paid rest breaks in accordance with California law to all its hourly employees regardless of their location as a gesture of employee goodwill, and due to our belief in the importance of workforce wellness.",
      "confidence": "high"
    },
    {
      "rule_id": "rest_break_denial_salaried",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.pay_type",
          "operator": "neq",
          "value": "hourly"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "required rest break",
        "parameters": {}
      },
      "source_text": "Salaried (exempt) employees are not required to take rest breaks, although they may take them at their discretion.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp Employee Handbook",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "termination_break_policy_violation",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employer.location_state",
          "operator": "in",
          "value": [
            "CA",
            "AZ",
            "TX",
            "NY",
            "FL"
          ]
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Disciplinary action, up to and including termination of employment, particularly for managers or supervisors who knowingly prevent employees from taking legally required breaks",
        "parameters": {}
      },
      "source_text": "Failure to adhere to this policy may result in disciplinary action, up to and including termination of employment, particularly for managers or supervisors who knowingly prevent employees from taking legally required breaks.",
      "confidence": "medium"
    },
    {
      "rule_id": "california_meal_break_requirement",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 5
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "a thirty (30) minute unpaid meal break, commencing no later than the end of your fifth hour of work",
        "parameters": {
          "break.type": "meal",
          "break.duration_minutes": 30
        }
      },
      "source_text": "For employees working in California, if you are a non-exempt employee and work more than five (5) hours in a workday, you are entitled to a thirty (30) minute unpaid meal break, commencing no later than the end of your fifth hour of work.",
      "confidence": "high"
    },
    {
      "rule_id": "california_second_meal_break",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 10
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "A second 30-minute unpaid meal break",
        "parameters": {
          "break.type": "meal",
          "break.duration_minutes": 30
        }
      },
      "source_text": "A second 30-minute unpaid meal break is required if you work more than ten (10) hours in a workday.",
      "confidence": "high"
    },
    {
      "rule_id": "california_rest_break",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gte",
          "value": 2
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "a paid ten (10) minute rest break",
        "parameters": {
          "break.type": "rest",
          "break.duration_minutes": 10
        }
      },
      "source_text": "California employees are also entitled to a paid ten (10) minute rest break for every four (4) hours worked, or major fraction thereof. For example, if you work over 2 hours but less than 4 hours, you are entitled to a rest break.",
      "confidence": "medium"
    }
  ],
  "metadata": {
    "policy_name": "Employee Handbook",
    "effective_date": "2024-01-15",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_federal_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 100
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure"
          ]
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 days advance written notice to affected employees",
        "parameters": {}
      },
      "source_text": "In the event of a plant closing or mass layoff as defined under the Federal WARN Act, where Acme Corp. employs 100 or more employees company-wide, excluding part-time employees, the company shall provide at least sixty (60) calendar days advance written notice to affected employees.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_california_002",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure",
            "relocation"
          ]
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 day notice period",
        "parameters": {}
      },
      "source_text": "Notwithstanding subsection (a), for employees whose primary work location is in the State of California, a sixty (60) day notice period shall apply if Acme Corp. employs, or has employed within the 36 months preceding the date of the planned action, 75 or more employees, and a mass layoff, relocation or termination of a covered establishment occurs. A mass layoff under California law means a layoff during any 30-day period of 50 or more employees.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_california_exception",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.type",
          "operator": "not_in",
          "value": [
            "business_circumstances",
            "unforeseeable_business_circumstances"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Documented exception from VP of HR",
        "parameters": {}
      },
      "source_text": "If the employment loss is a result of a physical disaster or act of war, such as but not limited to an earthquake, flood, fire, storm, or terrorist attack, the notice requirements may be waived by the Vice President of Human Resources, subject to the requirements of Section 3.14(d).",
      "confidence": "medium"
    },
    {
      "rule_id": "warn_notice_temporary_exception",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "layoff.timeframe_days",
          "operator": "lte",
          "value": 180
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "Requirements of Section 3.14",
        "parameters": {}
      },
      "source_text": "The requirements of this Section 3.14 do not apply to temporary cessation of employment when work is expected to resume within six months, or when a employee is laid off, and recalled within six months.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. HR Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_ca_termination_timing",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
