{
  "rules": [
    {
      "rule_id": "final_pay_involuntary_termination",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "final paycheck to be made available immediately upon termination if feasible, or mailed within 24 hours if not.",
        "parameters": {}
      },
      "source_text": "If an employee is involuntarily terminated from the Company, their final paycheck, inclusive of all earned and unpaid wages, will be made available to them immediately upon termination, if feasible. We will make every reasonable effort to comply with this requirement. However, if immediate payment is not administratively possible, we will mail the final check to the employee's last known address within 24 hours.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_voluntary_termination_notice",
