{
  "rules": [
    {
      "rule_id": "fmla_termination_ineligible_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.tenure_months",
          "operator": "lt",
          "value": 12
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "FMLA leave",
        "parameters": {}
      },
      "source_text": "In situations where an employee in the state of California has been terminated from Acme Corp, and the employee's tenure was under 12 months, the employee is ineligible to apply for FMLA. In such cases, the employee should not be granted FMLA leave.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Employee Handbook",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "ca_missed_break_pay_001",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "terminated"
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "laid_off"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Employer to pay one (1) hour of pay at the employee\u2019s regular rate of compensation for each workday that the meal or rest period is not provided.",
        "parameters": {}
      },
      "source_text": "Pursuant to California Labor Code Section 226.7, if the Company fails to provide an employee with a meal or rest period in accordance with this policy, the Company shall pay the employee one (1) hour of pay at the employee\u2019s regular rate of compensation for each workday that the meal or rest period is not provided. However, this provision shall not apply to employees whose employment status is terminated or laid off.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Human Resources Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_involuntary_termination_immediate",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck distribution",
        "parameters": {
          "timing": "immediately upon separation"
        }
      },
      "source_text": "employees who are involuntarily terminated from Global Dynamics Corp. shall receive their final paycheck, including all earned and unpaid wages, commissions, and accrued vacation time, immediately upon separation.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_involuntary_termination_72_hours",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck distribution",
        "parameters": {
          "timing": "as soon as reasonably practicable, but in no case later than 72 hours following the termination"
        }
      },
      "source_text": "However, where immediate payment is not feasible due to circumstances beyond the control of Global Dynamics Corp., such as payroll processing limitations, the final paycheck shall be provided as soon as reasonably practicable, but in no case later than 72 hours following the termination.",
      "confidence": "medium"
    },
    {
      "rule_id": "final_pay_voluntary_termination_with_notice",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "voluntary"
        },
        {
          "field": "termination.notice_given",
          "operator": "eq",
          "value": true
        },
        {
          "field": "termination.notice_hours",
          "operator": "gte",
          "value": 72
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck distribution",
        "parameters": {
          "timing": "on their last day of employment"
        }
      },
      "source_text": "Employees who provide such notice shall receive their final paycheck on their last day of employment.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_voluntary_termination_without_notice",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "voluntary"
        },
        {
          "field": "termination.notice_given",
          "operator": "eq",
          "value": false
        },
        {
          "field": "termination.notice_hours",
          "operator": "lt",
          "value": 72
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck distribution",
        "parameters": {
          "timing": "within seventy-two (72) hours of their last day of employment"
        }
      },
      "source_text": "Employees who voluntarily resign without providing at least seventy-two (72) hours\u2019 advance notice shall receive their final paycheck within seventy-two (72) hours of their last day of employment.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_salaried_employee_payroll_date",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.pay_type",
          "operator": "eq",
          "value": "salaried"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck distribution",
        "parameters": {
          "timing": "company's standard payroll date"
        }
      },
      "source_text": "These requirements notwithstanding, salaried employees will have their final paycheck issued on the company's standard payroll date, provided that date is within 72 hours of their termination.",
      "confidence": "medium"
    }
  ],
  "metadata": {
    "policy_name": "Global Dynamics Corp. \u2013 Human Resources Policy Manual",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_001",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 100
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure"
          ]
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 calendar days advance written notice of any plant closing or mass layoff to affected employees",
        "parameters": {
          "notice_period_days": 60
        }
      },
      "source_text": "It is Starlight Technologies' policy to comply with the federal WARN Act, which requires the company to provide sixty (60) calendar days advance written notice of any plant closing or mass layoff to affected employees.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_002",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "lt",
          "value": 100
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "company is exempt from the WARN Act requirements",
        "parameters": {}
      },
      "source_text": "If Starlight Technologies has fewer than 100 employees company-wide, we are exempt from the WARN Act requirements, and no such advance notice is required.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_003",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "layoff.affected_employee_count",
          "operator": "lt",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "the 60-day notice requirement does not apply",
        "parameters": {
          "notice_period_days": 60
        }
      },
      "source_text": "Furthermore, if a layoff affects less than 50 employees at a single employment site, the 60-day notice requirement does not apply, regardless of the company\u2019s total number of employees.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_004",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 100
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "lt",
          "value": 20
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "Part-time employees, defined as those working fewer than 20 hours per week or who have been employed for less than 6 months, are not counted when determining whether the thresholds for WARN Act applicability have been met.",
        "parameters": {}
      },
      "source_text": "Part-time employees, defined as those working fewer than 20 hours per week or who have been employed for less than 6 months, are not counted when determining whether the thresholds for WARN Act applicability have been met.",
      "confidence": "medium"
    }
  ],
  "metadata": {
    "policy_name": "Workforce Adjustment and Notification",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_ca_daily",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay",
        "parameters": {
          "overtime_rate": "1.5x regular rate of pay"
        }
      },
      "source_text": "If a non-exempt employee in California works more than eight (8) hours in a workday, that employee will be compensated one and one-half (1.5) times their regular rate of pay for all hours worked over eight (8) in that day.",
      "confidence": "medium"
    }
  ],
  "metadata": {
    "policy_name": "Skyline Dynamics HR Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_involuntary_termination_001",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "immediate payment of all wages due at the time of discharge",
        "parameters": {}
      },
      "source_text": "Pursuant to California Labor Code Section 201, if an employee is involuntarily terminated from Acme Corp., all wages earned and unpaid at the time of termination shall be due and payable immediately at the time of discharge.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_voluntary_resignation_notice_001",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "voluntary"
        },
        {
          "field": "termination.notice_given",
          "operator": "eq",
          "value": true
        },
        {
          "field": "termination.notice_hours",
          "operator": "gte",
          "value": 72
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "payment of all wages due at the time of resignation",
        "parameters": {}
      },
      "source_text": "Pursuant to California Labor Code Section 202, if an employee voluntarily resigns from Acme Corp., the timing of the final wage payment depends on the amount of notice provided. If the employee provides at least 72 hours' advance notice of their resignation, the final wages are due and payable at the time of resignation.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_voluntary_resignation_no_notice_001",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "voluntary"
        },
        {
          "field": "termination.notice_given",
          "operator": "eq",
          "value": false
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "payment of all wages due within 72 hours of the employee's last day of work",
        "parameters": {}
      },
      "source_text": "However, if the employee fails to provide at least 72 hours' notice, final wages are due and payable within 72 hours of the employee's last day of work.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Human Resources Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_authorization_termination_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "gt",
          "value": 45
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "employee employment may be terminated",
        "parameters": {}
      },
      "source_text": "Specifically, if a non-exempt employee fails to obtain prior authorization for overtime work and exceeds 45 hours worked in a workweek, and this violation is documented for a second time within a 12-month period, their employment may be terminated.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp Employee Handbook - Overtime Compensation",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "ca_daily_ot_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime compensation for hours exceeding 8 in a workday",
        "parameters": {}
      },
      "source_text": "Non-exempt employees working in California shall be compensated for overtime at a rate of one and one-half (1.5) times their regular rate of pay for all hours worked in excess of eight (8) hours in any workday, up to and including twelve (12) hours.",
      "confidence": "high"
    },
    {
      "rule_id": "ca_daily_ot_rate_002",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 12
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime compensation at 2.0x rate for hours exceeding 12 in a workday",
        "parameters": {}
      },
      "source_text": "Any hours worked beyond twelve (12) hours in a single workday shall be compensated at a rate of two (2.0) times the employee's regular rate of pay.",
      "confidence": "high"
    },
    {
      "rule_id": "ca_weekly_ot_eligibility_003",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "gt",
          "value": 40
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime compensation for hours exceeding 40 in a workweek",
        "parameters": {}
      },
      "source_text": "In addition to the daily overtime provisions outlined above, non-exempt employees working in California shall also be compensated for overtime at a rate of one and one-half (1.5) times their regular rate of pay for all hours worked in excess of forty (40) hours in any workweek.",
      "confidence": "high"
    },
    {
      "rule_id": "ca_seventh_day_ot_004",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.consecutive_workdays",
          "operator": "eq",
          "value": 7
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime compensation at 2.0x rate for hours exceeding 8 on the 7th consecutive day",
        "parameters": {}
      },
      "source_text": "Furthermore, if a non-exempt employee works more than eight (8) hours on the seventh consecutive day in a workweek, those hours exceeding eight (8) on that day shall be compensated at a rate of two (2.0) times the employee's regular rate of pay.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Bright Future Corp. Human Resources Policy Manual",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_california_mass_layoff",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "layoff.timeframe_days",
          "operator": "lte",
          "value": 30
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 days advance written notice to affected employees, the California Employment Development Department (EDD), and the local government",
        "parameters": {}
      },
      "source_text": "Therefore, if Acme Corp has 75 or more employees, and a mass layoff of 50 or more employees is planned at a single site of employment within California within a 30-day timeframe, the company is required to provide 60 days advance written notice to affected employees, the California Employment Development Department (EDD), and the local government.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp Human Resources Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_ca_daily_001",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "overtime pay at 1.5x or 2x the regular rate, depending on hours worked over 8 in a day and on 7th consecutive day worked",
        "parameters": {}
      },
      "source_text": "However, for employees whose *employee.location_state* is California, AlphaCorp provides overtime pay for all hours worked over eight (8) in a workday and for the first eight hours worked on the seventh (7th) consecutive day of work in a workweek (see Policy 3.2.5 regarding rest periods). Overtime compensation will be paid at a rate of one and one-half (1.5) times the employee\u2019s regular rate of pay for hours worked beyond eight (8) hours in a workday, up to twelve (12) hours in a workday and for the first eight (8) hours on the seventh (7th) consecutive day worked in a workweek if the *employee.location_state* is California. Furthermore, non-exempt employees in California will receive overtime compensation at a rate of two (2) times their regular rate of pay for all hours worked over twelve (12) in a workday and for all hours worked over eight (8) on the seventh (7th) consecutive day worked in a workweek.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "AlphaCorp Employee Handbook",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_pay_001",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "gt",
          "value": 40
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay at 1.5 times regular rate",
        "parameters": {}
      },
      "source_text": "If an employee.classification is non_exempt and employee.hours_worked_weekly is greater than 40, then Acme Corp. will grant compensation at 1.5 times their regular rate of pay for those overtime hours.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Overtime Compensation",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "meal_break_entitlement_001",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 5
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "30-minute unpaid meal break",
        "parameters": {
          "break.type": "meal",
          "break.duration_minutes": 30
        }
      },
      "source_text": "Non-exempt employees are generally entitled to a 30-minute unpaid meal break if they work more than five (5) hours in a workday.",
      "confidence": "high"
    },
    {
