{
  "rules": [
    {
      "rule_id": "overtime_pay_flsa",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "neq",
          "value": "CA"
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "gt",
          "value": 40
        },
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay",
        "parameters": {
          "rate": "1.5"
        }
      },
      "source_text": "For non-exempt employees located outside of California, overtime shall be calculated at one and one-half (1.5) times the employee's regular rate of pay for all hours worked in excess of forty (40) in a workweek.",
      "confidence": "high"
    },
    {
      "rule_id": "overtime_pay_ca_daily",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "lte",
          "value": 12
        },
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay",
        "parameters": {
          "rate": "1.5"
        }
      },
      "source_text": "Specifically, for California employees, overtime compensation will be paid at one and one-half (1.5) times the employee's regular rate of pay for all hours worked in excess of eight (8) in a workday or forty (40) in a workweek.",
      "confidence": "high"
    },
    {
      "rule_id": "overtime_pay_ca_weekly",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "gt",
          "value": 40
        },
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay",
        "parameters": {
          "rate": "1.5"
        }
      },
      "source_text": "Specifically, for California employees, overtime compensation will be paid at one and one-half (1.5) times the employee's regular rate of pay for all hours worked in excess of eight (8) in a workday or forty (40) in a workweek.",
      "confidence": "high"
    },
    {
      "rule_id": "overtime_pay_ca_double_time",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 12
        },
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay",
        "parameters": {
          "rate": "2"
        }
      },
      "source_text": "Employees who work more than twelve (12) hours in a workday or more than eight (8) hours on the seventh (7th) consecutive day of a workweek shall be compensated at two (2) times their regular rate of pay.",
      "confidence": "high"
    },
    {
      "rule_id": "overtime_pay_ca_double_time_seventh_day",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.consecutive_workdays",
          "operator": "eq",
          "value": 7
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        },
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "overtime pay",
        "parameters": {
          "rate": "2"
        }
      },
      "source_text": "Employees who work more than twelve (12) hours in a workday or more than eight (8) hours on the seventh (7th) consecutive day of a workweek shall be compensated at two (2) times their regular rate of pay.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Sunrise Technologies Employee Handbook",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_001",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employer.employee_count_within_75_miles",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure",
            "relocation"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "affected employees",
        "parameters": {
          "notification_period_days": 60
        }
      },
      "source_text": "Therefore, if Acme Corp has at least 75 employees in California, and is conducting a mass layoff of 50 or more employees at a single site, then we are required to notify affected employees at least 60 days prior to the layoff.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_002",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 100
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure",
            "relocation"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "employees",
        "parameters": {
          "notification_period_days": 60
        }
      },
      "source_text": "If Acme Corp has at least 100 employees company-wide, and conducts a mass layoff of 50 or more employees at a single site in California, then we are also required to notify employees at least 60 days in advance.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp HR Policy Manual",
    "effective_date": "2024-02-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_ca_employees",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "overtime compensation for hours exceeding 8 in a day or 40 in a week",
        "parameters": {}
      },
      "source_text": "This section applies solely to non-exempt employees physically working within the State of California. Pursuant to California Labor Code Sections 510 and 515, Acme Corp. shall compensate eligible employees for overtime hours worked. Overtime shall be defined as all hours worked in excess of eight (8) hours in any one workday or forty (40) hours in any one workweek.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Human Resources Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_paycheck_timing_001",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.hours_worked_12_months",
          "operator": "lt",
          "value": 60
        },
        {
          "field": "employee.tenure_months",
          "operator": "lt",
          "value": 3
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "final paycheck issuance",
        "parameters": {
          "timeframe": "within fourteen (14) days of the termination date"
        }
      },
      "source_text": "Notwithstanding the above, and to ensure compliance with applicable federal standards, if an employee has worked fewer than 60 hours in the 12 months preceding termination, and if the employee's tenure at Acme Corp was less than 3 months, then the final paycheck may be issued within fourteen (14) days of the termination date.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp HR Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_001",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "lt",
          "value": 100
        },
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "sixty (60) calendar days advance written notice to affected employees prior to any plant closing or mass layoff",
        "parameters": {}
      },
      "source_text": "However, notwithstanding the foregoing, if the employer.employee_count is less than 100, yet equal to or greater than 75, and the employee.location_state is California, and if a mass layoff, plant closing or relocation affects 50 or more employees, then the Company will provide the aforementioned sixty (60) day notice.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Workforce Reductions and WARN Act Compliance",
    "effective_date": "2025-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_pay_001",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "employee.employee_classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "gt",
          "value": 40
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "overtime pay at 1.5 times the regular rate",
        "parameters": {}
      },
      "source_text": "If a non-exempt employee's hours_worked_weekly exceeds 40, the employee will receive 1.5 times their regular hourly rate for each hour over 40.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Overtime Compensation",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_california",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "any",
      "action": {
        "type": "notify",
        "subject": "affected employees, designated employee representatives, CA EDD, and the chief elected official of the local government",
        "parameters": {
          "notice_period_days": 60
        }
      },
      "source_text": "Notwithstanding Section 3.12.3, for employees whose primary work location is in California, GlobalTech Solutions, Inc. shall provide such notice at least sixty (60) calendar days prior to the date of the plant closing, mass layoff, or relocation if either (a) the employer has seventy-five (75) or more employees or (b) fifty (50) or more employees are affected by said mass layoff, plant closure, or relocation.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_federal",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "neq",
          "value": "CA"
        },
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 100
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "affected employees, designated employee representatives, CA EDD, and the chief elected official of the local government",
        "parameters": {
          "notice_period_days": 60
        }
      },
      "source_text": "For work locations outside of California, GlobalTech Solutions, Inc. shall provide such notice at least sixty (60) calendar days prior to the date of the plant closing, mass layoff, or relocation if the employer has one hundred (100) or more employees and the plant closing or mass layoff affects fifty (50) or more employees at a single site of employment, or a relocation occurs that results in an employment loss for fifty (50) or more employees at a single site of employment within any thirty (30) day period.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_no_federal",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "neq",
          "value": "CA"
        },
        {
          "field": "employer.employee_count",
          "operator": "lt",
          "value": 100
        },
        {
          "field": "employer.employee_count",
          "operator": "gt",
          "value": 74
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "lt",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "affected employees, designated employee representatives, CA EDD, and the chief elected official of the local government",
        "parameters": {
          "notice_period_days": 0
        }
      },
      "source_text": "For work locations outside of California, if the employer has less than one hundred (100) employees but more than seventy four (74) employees and a mass layoff impacts fewer than fifty employees, then no WARN notice is required.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Workforce Reduction and WARN Act Compliance",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "ca_pfl_eligibility_001",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": true
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "leave.type",
          "operator": "in",
          "value": [
            "bonding",
            "caregiver"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "Up to 8 weeks of partial wage replacement through California PFL",
        "parameters": {}
      },
      "source_text": "Eligible California employees who contribute to State Disability Insurance (SDI) may be able to receive up to 8 weeks of partial wage replacement (approximately 60-70% of your normal pay, as determined by the state) through California's PFL program. This benefit is for bonding with a new child (within one year of birth, adoption, or foster care placement) or caring for a seriously ill family member (child, parent, spouse, registered domestic partner, grandparent, grandchild, or sibling). To be eligible for PFL, your employee.sdi_contributor status must be set to 'true' and your employee.location_state must be equal to 'CA'. The leave.type must be either 'bonding' or 'caregiver'.",
      "confidence": "high"
    },
    {
      "rule_id": "fmla_eligibility_001",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.tenure_months",
          "operator": "gte",
          "value": 12
        },
        {
          "field": "employee.hours_worked_12_months",
          "operator": "gte",
          "value": 1250
        },
        {
          "field": "employer.employee_count_within_75_miles",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "Up to 12 weeks of unpaid, job-protected leave under the FMLA",
        "parameters": {}
      },
      "source_text": "employees who have worked for Pacific Dynamics for at least 12 months (employee.tenure_months >= 12) and have worked at least 1,250 hours in the 12 months prior to the leave start date (employee.hours_worked_12_months >= 1250), and whose employee.employment_status is 'active', are eligible for up to 12 weeks of unpaid, job-protected leave under the FMLA. This only applies if Pacific Dynamics employs 50 or more employees within a 75-mile radius of your worksite (employer.employee_count_within_75_miles >= 50)",
      "confidence": "high"
    },
    {
      "rule_id": "pfl_geography_restriction_001",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "neq",
          "value": "CA"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "referral to Policy Section 3.3 for state-specific leave policies",
        "parameters": {}
      },
      "source_text": "Employees whose employee.location_state is not 'CA' will need to refer to Policy Section 3.3 for state-specific leave policies.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Pacific Dynamics HR Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_federal_standard",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "not_in",
          "value": [
            "CA"
          ]
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "active"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "final paycheck distribution",
        "parameters": {
          "timing": "no later than the next regular payday following the last day of employment"
        }
      },
      "source_text": "However, in situations where an employee is terminated involuntarily, and the employee's location_state is not in the list of states which mandate immediate payment (including but not limited to California, as defined under that state's Labor Code Sections 201 and 202), and provided the employee's employment_status is not \"active\", the final paycheck will be processed, and available for receipt, no later than the next regular payday following the last day of employment.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Final Paycheck Distribution",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "terminate_meal_break_violation_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "disciplinary action, up to and including termination of employment",
        "parameters": {}
      },
      "source_text": "Failure to comply with this policy, where the employee's actions contribute to the missed break (e.g., rejecting a properly offered break, and reporting such refusal to GGT), may result in disciplinary action, up to and including termination of employment.",
      "confidence": "medium"
    },
    {
      "rule_id": "terminate_rest_break_violation_002",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "disciplinary action up to and including termination",
        "parameters": {}
      },
      "source_text": "If an employee demonstrates a consistent pattern of being unable or unwilling to take breaks, despite GGT\u2019s good faith efforts to provide such breaks, and these actions disrupt normal business operations, the employee may be subject to disciplinary action up to and including termination.",
      "confidence": "medium"
    }
  ],
  "metadata": {
    "policy_name": "Employee Handbook",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_paycheck_timing_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.tenure_months",
          "operator": "lte",
          "value": 12
        },
        {
          "field": "employee.employment_status",
          "operator": "in",
          "value": [
            "terminated",
            "resigned"
          ]
        },
        {
          "field": "termination.type",
          "operator": "in",
          "value": [
            "involuntary",
            "voluntary"
          ]
        },
        {
          "field": "employee.hours_worked_weekly",
          "operator": "lt",
          "value": 20
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck available within 72 hours of separation",
        "parameters": {}
      },
      "source_text": "If, however, the employee has been employed for less than twelve (12) months and their employment status changes to terminated due to involuntary separation or resignation, and the employee has worked less than 20 hours per week on average over their total tenure with the company, the final paycheck will be available within 72 hours of the separation.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "HR Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}
