{
  "rules": [
    {
      "rule_id": "warn_notice_federal",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 100
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure",
            "relocation"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 days advance written notice to affected employees, their representatives (if any), the State of California Employment Development Department (EDD), and the chief elected official of the local government.",
        "parameters": {
          "notice_period_days": 60
        }
      },
      "source_text": "If Acme Co. employs 100 or more employees (nationwide), and a mass layoff or plant closure affecting 50 or more employees is planned, the company must provide at least 60 calendar days' advance written notice to affected employees, their representatives (if any), the State of California Employment Development Department (EDD), and the chief elected official of the local government.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_california",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure",
            "relocation"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 days advance written notice to affected employees, their representatives (if any), the State of California Employment Development Department (EDD), and the chief elected official of the local government.",
        "parameters": {
          "notice_period_days": 60
        }
      },
      "source_text": "If the company employs 75 or more employees in California, and a mass layoff or plant closure affecting 50 or more employees occurs in California, the company must comply with California WARN regulations, providing 60 days' notice as described above.",
      "confidence": "high"
    },
    {
      "rule_id": "warn_notice_ca_precedence",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "apply the California WARN act requirements",
        "parameters": {}
      },
      "source_text": "In situations where the employee is located in California, Acme Co. will always apply the California WARN act requirements.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Co. Human Resources Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "pfl_sdi_contribution_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": true
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "eligibility for California Paid Family Leave (PFL) benefits",
        "parameters": {}
      },
      "source_text": "Eligibility for PFL is contingent upon the employee's participation in and contribution to the California State Disability Insurance (SDI) program, as mandated by California law. Only employees designated as SDI contributors, as determined by payroll records, shall be considered eligible.",
      "confidence": "high"
    },
    {
      "rule_id": "pfl_sdi_contribution_002",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": false
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "eligibility for California Paid Family Leave (PFL) benefits",
        "parameters": {}
      },
      "source_text": "If the employee does not contribute to SDI (employee.sdi_contributor is false), then they are not eligible for PFL.",
      "confidence": "high"
    },
    {
      "rule_id": "pfl_employment_status_003",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "eligibility for California Paid Family Leave (PFL) benefits",
        "parameters": {}
      },
      "source_text": "Notwithstanding any other provision within this Section, only active employees, those with an employment_status of 'active', are eligible to receive PFL benefits.",
      "confidence": "high"
    },
    {
      "rule_id": "pfl_employment_status_004",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "in",
          "value": [
            "terminated",
            "resigned",
            "laid_off"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "eligibility for California Paid Family Leave (PFL) benefits",
        "parameters": {}
      },
      "source_text": "Employees who are terminated, resigned, or laid off are ineligible for PFL.",
      "confidence": "high"
    },
    {
      "rule_id": "pfl_leave_duration_005",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "leave.type",
          "operator": "in",
          "value": [
            "family",
            "medical",
            "parental",
            "bonding",
            "caregiver"
          ]
        },
        {
          "field": "leave.duration_weeks",
          "operator": "gt",
          "value": 8
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "PFL benefits duration",
        "parameters": {
          "max_duration_weeks": 8
        }
      },
      "source_text": "Consistent with California law, the maximum duration of PFL benefits shall not exceed eight (8) weeks within a twelve (12) month period.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Employee Handbook",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "pfl_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": true
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "eligibility for California Paid Family Leave (PFL)",
        "parameters": {}
      },
      "source_text": "Employees who contribute to California State Disability Insurance (SDI) and work in California are generally eligible for California Paid Family Leave (PFL).",
      "confidence": "high"
    },
    {
      "rule_id": "termination_pfl_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "active"
        },
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": true
        },
        {
          "field": "leave.type",
          "operator": "eq",
          "value": "family"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "termination of employment",
        "parameters": {}
      },
      "source_text": "An employee will not be terminated if their employment status is active and SDI contributions are being made. If the employee contributes to SDI, and is actively employed, they will not be terminated for using PFL as defined in this section.",
      "confidence": "high"
    },
    {
      "rule_id": "fmla_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employer.employee_count_within_75_miles",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "eligibility for Family and Medical Leave Act (FMLA)",
        "parameters": {}
      },
      "source_text": "The Family and Medical Leave Act (FMLA) is a federal law providing eligible employees of Sterling Consolidated (where we have 50 or more employees within a 75-mile radius of your worksite) with up to 12 weeks of unpaid, job-protected leave per 12-month period for specified family and medical reasons.",
      "confidence": "high"
    },
    {
      "rule_id": "termination_fmla_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "active"
        },
        {
          "field": "leave.type",
          "operator": "in",
          "value": [
            "family",
            "medical"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "consideration of termination",
        "parameters": {}
      },
      "source_text": "Sterling Consolidated may consider termination during an active FMLA or PFL leave period if the employee's employment status changes from active to something else.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Sterling Consolidated HR Policy Manual",
    "effective_date": "2024-02-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "meal_break_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.shift_hours",
          "operator": "gt",
          "value": 5
        },
        {
          "field": "employee.classification",
          "operator": "not_in",
          "value": [
            "exempt"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "unpaid 30 minute meal break",
        "parameters": {
          "break.type": "meal",
          "break.duration_minutes": 30
        }
      },
      "source_text": "If a non-exempt employee works in California and their shift hours are greater than 5, AND they are *not* classified as exempt, then they are eligible for an unpaid 30 minute meal break.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Employee Handbook",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_001",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employer.employee_count",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "60 days' notice of the layoff",
        "parameters": {}
      },
      "source_text": "Therefore, if Apex Corporation has 75 or more employees within California, and a layoff will impact 50 or more employees, affected employees are entitled to 60 days' notice of the layoff.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Workforce Reduction and WARN Act Compliance",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_ca_involuntary",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "terminated"
        },
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck, encompassing all wages earned and accrued vacation time, to be made available to the employee immediately upon termination.",
        "parameters": {}
      },
      "source_text": "Pursuant to California Labor Code Section 201, notwithstanding any other provision of this Section, if an employee.location_state is 'CA' and employee.employment_status is 'terminated' through involuntary termination, the employee\u2019s final paycheck, encompassing all wages earned and accrued vacation time, shall be made available to the employee immediately upon termination.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_ca_voluntary_notice",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "resigned"
        },
        {
          "field": "termination.notice_given",
          "operator": "eq",
          "value": "true"
        },
        {
          "field": "termination.notice_hours",
          "operator": "gte",
          "value": 72
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck to be made available on the employee\u2019s last day of employment.",
        "parameters": {}
      },
      "source_text": "If an employee.location_state is 'CA' and employee.employment_status is 'resigned', and termination.notice_given is 'true' with termination.notice_hours equal to or greater than 72, the final paycheck shall be made available on the employee\u2019s last day of employment.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_ca_voluntary_no_notice",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "eq",
          "value": "resigned"
        },
        {
          "field": "termination.notice_hours",
          "operator": "lt",
          "value": 72
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Acme Corp. shall make the final paycheck available to the employee within 72 hours of their last day of employment.",
        "parameters": {}
      },
      "source_text": "If termination.notice_hours is less than 72 hours, Acme Corp. shall make the final paycheck available to the employee within 72 hours of their last day of employment.",
      "confidence": "high"
    },
    {
      "rule_id": "final_pay_federal",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "neq",
          "value": "CA"
        },
        {
          "field": "employee.employment_status",
          "operator": "not_in",
          "value": [
            "terminated",
            "resigned",
            "laid_off"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final pay will be disbursed on the next regularly scheduled payday",
        "parameters": {}
      },
      "source_text": "For employees in states other than California whose employee.employment_status is not 'terminated', 'resigned' or 'laid_off', final pay will be disbursed on the next regularly scheduled payday, as required by federal law.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Human Resources Policy Manual",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "pfl_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": true
        },
        {
          "field": "leave.type",
          "operator": "in",
          "value": [
            "family",
            "bonding",
            "caregiver"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "Eligibility for California Paid Family Leave (PFL)",
        "parameters": {
          "duration_weeks": 8,
          "wage_replacement_percentage": "60-70"
        }
      },
      "source_text": "California PFL, on the other hand, provides *partial wage replacement* (currently 60-70% of your usual wages) for up to 8 weeks for bonding with a new child or caring for a seriously ill family member (child, parent, spouse, grandparent, grandchild, sibling, or registered domestic partner). To be eligible for CA PFL, you must contribute to California State Disability Insurance (SDI).",
      "confidence": "high"
    },
    {
      "rule_id": "pfl_ineligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.sdi_contributor",
          "operator": "eq",
          "value": false
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "Eligibility for California Paid Family Leave (PFL)",
        "parameters": {}
      },
      "source_text": "Employees who are not SDI contributors are not eligible for PFL benefits, even if they are eligible for FMLA.",
      "confidence": "high"
    },
    {
      "rule_id": "fmla_eligibility_001",
      "rule_type": "eligibility",
      "conditions": [
        {
          "field": "employer.employee_count_within_75_miles",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "employee.tenure_months",
          "operator": "gte",
          "value": 12
        },
        {
          "field": "employee.hours_worked_12_months",
          "operator": "gte",
          "value": 1250
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "grant",
        "subject": "Eligibility for federal Family and Medical Leave Act (FMLA)",
        "parameters": {
          "duration_weeks": 12,
          "compensation_type": "unpaid"
        }
      },
      "source_text": "FMLA applies to employees who have worked for Sterling Innovations for at least 12 months, have worked at least 1,250 hours in the 12 months before the leave, and if Sterling Innovations has 50 or more employees within a 75-mile radius of your worksite.",
      "confidence": "high"
    },
    {
      "rule_id": "fmla_pfl_concurrent_001",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "leave.type",
          "operator": "in",
          "value": [
            "family",
            "medical",
            "parental",
            "bonding",
            "caregiver"
          ]
        },
        {
          "field": "employee.sdi_contributor",
          "operator": "neq",
          "value": false
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "FMLA runs concurrently with PFL where applicable.",
        "parameters": {}
      },
      "source_text": "Note that, depending on the timing and overlap between FMLA and PFL, FMLA runs concurrently with PFL where applicable.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "HR Policy Manual",
    "effective_date": "2024-07-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "meal_break_unpaid_if_30_minutes",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "break.type",
          "operator": "eq",
          "value": "meal"
        },
        {
          "field": "break.duration_minutes",
          "operator": "gte",
          "value": 30
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "meal break must be unpaid",
        "parameters": {}
      },
      "source_text": "However, if an employee voluntarily takes a meal break of thirty (30) minutes or more, they must be fully relieved of all job duties during that period. Such a meal break shall be unpaid.",
      "confidence": "high"
    },
    {
      "rule_id": "voluntary_meal_break_no_obligation",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "break.type",
          "operator": "eq",
          "value": "meal"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "obligation for compensation in lieu of break",
        "parameters": {}
      },
      "source_text": "An employee's decision to waive their meal break does not create an obligation for Acme Corp. to provide any compensation in lieu of the break, nor does it expose the company to any penalty.",
      "confidence": "high"
    },
    {
      "rule_id": "rest_break_paid_if_5_to_20_minutes",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "break.type",
          "operator": "eq",
          "value": "rest"
        },
        {
          "field": "break.duration_minutes",
          "operator": "gte",
          "value": 5
        },
        {
          "field": "break.duration_minutes",
          "operator": "lte",
          "value": 20
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "rest break must be paid",
        "parameters": {}
      },
      "source_text": "However, should Acme Corp. elect to provide rest breaks lasting five (5) to twenty (20) minutes, any such rest breaks shall be considered time worked and shall be fully compensated.",
      "confidence": "high"
    },
    {
      "rule_id": "rest_break_refusal_no_discipline",
      "rule_type": "restriction",
      "conditions": [
        {
          "field": "break.type",
          "operator": "eq",
          "value": "rest"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "deny",
        "subject": "disciplinary action for refusal to take rest break",
        "parameters": {}
      },
      "source_text": "Refusal to take an offered rest break shall not be grounds for disciplinary action.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Employee Handbook",
    "effective_date": "2025-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "warn_notice_california_threshold",
      "rule_type": "entitlement",
      "conditions": [
        {
          "field": "employer.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "layoff.type",
          "operator": "in",
          "value": [
            "mass_layoff",
            "plant_closure",
            "relocation"
          ]
        },
        {
          "field": "layoff.affected_employee_count",
          "operator": "gte",
          "value": 50
        },
        {
          "field": "employer.employee_count_within_75_miles",
          "operator": "gte",
          "value": 75
        },
        {
          "field": "employer.employee_count",
          "operator": "lt",
          "value": 100
        },
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        }
      ],
      "condition_logic": "all",
      "action": {
