{
  "rules": [
    {
      "rule_id": "meal_break_violation_termination_001",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "break.type",
          "operator": "eq",
          "value": "meal"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "disciplinary action, up to and including termination of employment",
        "parameters": {}
      },
      "source_text": "Failure to take or accurately record a meal break may result in disciplinary action, up to and including termination of employment, particularly if such failure results in a violation of applicable state or federal law.",
      "confidence": "high"
    },
    {
      "rule_id": "rest_break_violation_termination_002",
      "rule_type": "termination",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "break.type",
          "operator": "eq",
          "value": "rest"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "disciplinary action, including termination",
        "parameters": {}
      },
      "source_text": "As with meal breaks, failure to adhere to these policies and procedures may result in disciplinary action, including termination.",
      "confidence": "high"
    },
    {
      "rule_id": "notify_break_violation_003",
      "rule_type": "leave",
      "conditions": [
        {
          "field": "employee.classification",
          "operator": "eq",
          "value": "non_exempt"
        },
        {
          "field": "break.type",
          "operator": "not_in",
          "value": [
            "family",
            "medical",
            "parental",
            "bonding",
            "caregiver"
          ]
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "notify",
        "subject": "manager and Human Resources",
        "parameters": {}
      },
      "source_text": "Employees who feel they have been denied a rest or meal break should immediately notify their manager and Human Resources.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Employee Handbook",
    "effective_date": "2025-01-01",
    "applicable_jurisdictions": [
      "CA",
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "final_pay_involuntary_termination_001",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "termination.type",
          "operator": "eq",
          "value": "involuntary"
        },
        {
          "field": "employee.employment_status",
          "operator": "neq",
          "value": "active"
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "Final paycheck to be made available to the employee on their last day of employment.",
        "parameters": {}
      },
      "source_text": "However, if an employee is terminated involuntarily, and the employee's employment_status is not \"active\", then the final paycheck will be made available to the employee on their last day of employment.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Final Paycheck Procedures",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "federal"
    ]
  }
}

{
  "rules": [
    {
      "rule_id": "overtime_california_daily",
      "rule_type": "compensation",
      "conditions": [
        {
          "field": "employee.location_state",
          "operator": "eq",
          "value": "CA"
        },
        {
          "field": "employee.classification",
          "operator": "neq",
          "value": "exempt"
        },
        {
          "field": "employee.hours_worked_daily",
          "operator": "gt",
          "value": 8
        }
      ],
      "condition_logic": "all",
      "action": {
        "type": "require",
        "subject": "overtime compensation at 1.5x regular rate",
        "parameters": {}
      },
      "source_text": "Consistent with California law, if a non-exempt employee's location_state is 'CA', and their hours_worked_daily exceeds eight (8) hours in a single workday or their hours_worked_weekly exceeds forty (40) hours in a workweek, the employee will be compensated for those overtime hours at a rate of one and one-half (1.5) times their regular rate of pay.",
      "confidence": "high"
    }
  ],
  "metadata": {
    "policy_name": "Acme Corp. Employee Handbook",
    "effective_date": "2024-01-01",
    "applicable_jurisdictions": [
      "CA"
    ]
  }
}
